<?php   $terms = wp_get_object_terms(get_the_ID(), 'wpsisac_slider-category', array('fields'=>'ids'));  $term_id = array_pop($terms);   $sliderurl = get_post_meta( get_the_ID(),'wpsisac_slide_link', true ); if($term_id == 130 || $term_id == 131){      ?>    <div  class="slick-image-slide custom_slider_anchor" data-link="<?php echo $sliderurl; ?>"     <?php echo $slider_height_css ; ?> >     <?php } else{  ?>  <div  class="slick-image-slide"      <?php echo $slider_height_css ; ?> >   <?php }?><div style="background-image: url('<?php echo $slider_img; ?>')!important;" class="slide-wrap"      <?php echo $slider_height_css ; ?> >				<img src="<?php //echo $slider_img; ?>" alt="<?php //the_title(); ?>" />		<div  class="slider-overlay">			  <div class="slider-content">	  <?php    if($term_id == 130 || $term_id == 131){    if($show_content) { ?>					    <div class="slick-slider-short-content">						      <?php the_content(); ?>								    </div>				    <?php }    	?>			    <h2 class="slide-title">        <a class="custom_slider_anchor" target="_blank" href="<?php echo $sliderurl; ?>">        <?php the_title(); ?>      </a>    </h2>				    <?php    }else{      ?>      <h2 class="slide-title"><?php the_title(); ?></h2>      <?php if($show_content) { ?>          <div class="slick-slider-short-content">            <?php the_content(); ?>               </div>        <?php }     }  ?>			    <?php 	    			  if($term_id != 130){      if($term_id != 131){           if($sliderurl != '') { ?>				    <div class="readmore">      <a href="<?php echo $sliderurl; ?>" class="slider-readmore">        <?php esc_html_e( 'Learn More Here', 'wp-slick-slider-and-image-carousel' ); ?>      </a>    </div>				    <?php }     }  }?>			  </div>		</div>	</div></div>